import React, { useState } from 'react';
import Sidebar from './Sidebar';
import ModalManager from './ModalManager';
import StatsCard from './StatsCard';
import RecentOrders from './RecentOrders';
import ProductsList from './ProductsList';
import SalesChart from './Charts/SalesChart';
import CategoryChart from './Charts/CategoryChart';
import { dashboardData } from '../data/mockData';
import '../style/Dashboard.css';

const Dashboard = () => {
  const [activeView, setActiveView] = useState('dashboard');
  const [activeModal, setActiveModal] = useState(null);
  const { stats, recentOrders, products, salesData, categoryData } = dashboardData;

  const handleShowModal = (modalType) => {
    setActiveModal(modalType);
  };

  const handleCloseModal = () => {
    setActiveModal(null);
  };

  const handleModalAction = (action, data) => {
    console.log('AÃ§Ã£o do modal:', action, data);
    // Aqui vocÃª pode adicionar lÃ³gica para atualizar o estado baseado nas aÃ§Ãµes dos modais
  };

  const statsIcons = {
    sales: 'ðŸ’°',
    orders: 'ðŸ“¦',
    products: 'ðŸ“š',
    customers: 'ðŸ‘¥'
  };

  return (
    <div className="app">
      <Sidebar 
        activeView={activeView}
        setActiveView={setActiveView}
        onShowModal={handleShowModal}
      />

      <main className="main-content">
        <div className="dashboard">
          <header className="dashboard-header">
            <h1>ArcanoStore Dashboard</h1>
            <p>Painel de controle da loja de produtos geek</p>
          </header>

          {/* Cards de EstatÃ­sticas */}
          <div className="stats-grid">
            <StatsCard
              title="Total de Vendas"
              value={new Intl.NumberFormat('pt-BR', {
                style: 'currency',
                currency: 'BRL'
              }).format(stats.totalSales)}
              icon={statsIcons.sales}
              color="primary"
            />
            <StatsCard
              title="Total de Pedidos"
              value={stats.totalOrders}
              icon={statsIcons.orders}
              color="secondary"
            />
            <StatsCard
              title="Produtos Ativos"
              value={stats.totalProducts}
              icon={statsIcons.products}
              color="success"
            />
            <StatsCard
              title="Clientes Cadastrados"
              value={stats.totalCustomers}
              icon={statsIcons.customers}
              color="warning"
            />
          </div>

          {/* GrÃ¡ficos */}
          <div className="charts-grid">
            <div className="chart-wrapper">
              <SalesChart data={salesData} />
            </div>
            <div className="chart-wrapper">
              <CategoryChart data={categoryData} />
            </div>
          </div>

          {/* ConteÃºdo Inferior */}
          <div className="content-grid">
            <div className="content-column">
              <RecentOrders orders={recentOrders} />
            </div>
            <div className="content-column">
              <ProductsList products={products} />
            </div>
          </div>
        </div>
      </main>

      <ModalManager 
        activeModal={activeModal}
        onClose={handleCloseModal}
        onAction={handleModalAction}
      />
    </div>
  );
};

export default Dashboard;
