import React from 'react';
import { CssBaseline, ThemeProvider, createTheme } from '@mui/material';
import AdminDashboard from './components/AdminDashboard';

// Configuração básica do tema MUI
const theme = createTheme({
  palette: {
    primary: {
      main: '#556cd6',
    },
    secondary: {
      main: '#19857b',
    },
    background: {
      default: '#f4f6f8',
    },
  },
});

function App() {
  return (
    <ThemeProvider theme={theme}>
      {/* Reseta o CSS para um layout padrão (MUI) */}
      <CssBaseline /> 
      <AdminDashboard />
    </ThemeProvider>
  );
}

export default App;
