import React from 'react';
import { Box, Typography, Grid, Container } from '@mui/material';
import StatsCard from './StatsCard';
import RecentOrdersTable from './RecentOrdersTable';
import { AttachMoney, Group, ShoppingCart } from '@mui/icons-material';

function AdminDashboard() {
  return (
    <Box sx={{ flexGrow: 1, p: 3, bgcolor: '#f4f6f8', minHeight: '100vh' }}>
      <Container maxWidth="xl">
        <Typography variant="h4" component="h1" gutterBottom sx={{ mb: 4, fontWeight: 'bold' }}>
          üìä Painel de Administra√ß√£o
        </Typography>

        {/* --- Cart√µes de Estat√≠sticas --- */}
        <Grid container spacing={3} sx={{ mb: 4 }}>
          <Grid item xs={12} sm={6} md={4}>
            <StatsCard 
              title="Vendas Totais" 
              value="R$ 45.210" 
              icon={<AttachMoney />} 
              color="#4caf50" // Verde
            />
          </Grid>
          <Grid item xs={12} sm={6} md={4}>
            <StatsCard 
              title="Novos Usu√°rios" 
              value="1.352" 
              icon={<Group />} 
              color="#2196f3" // Azul
            />
          </Grid>
          <Grid item xs={12} sm={6} md={4}>
            <StatsCard 
              title="Pedidos Pendentes" 
              value="78" 
              icon={<ShoppingCart />} 
              color="#ff9800" // Laranja
            />
          </Grid>
        </Grid>

        {/* --- Se√ß√£o de Dados (Tabelas) --- */}
        <Grid container spacing={3}>
          {/* Pode adicionar um componente de Gr√°fico aqui (usando Chart.js ou Recharts) */}
          <Grid item xs={12} md={12}>
            <RecentOrdersTable />
          </Grid>
        </Grid>

      </Container>
    </Box>
  );
}

export default AdminDashboard;
