const API_BASE_URL = '../../back-end/servicesistema/';

class ApiService {
    async request(endpoint, options = {}) {
        const url = `${API_BASE_URL}${endpoint}`;

        try {
            const response = await fetch(url, {
                headers: {
                    'Content-Type': 'application/json',
                    ...options.headers,
                },
                ...options,
            });

            const data = await response.json();

            if (!data.success) {
                throw new Error(data.message || 'Erro na requisição');
            }

            return data;
        } catch (error) {
            console.error('API Error:', error);
            throw error;
        }
    }

    // Products
    async getProducts() {
        return this.request('?action=getProducts');
    }

    async getProduct(id) {
        return this.request(`?action=getProduct&id=${id}`);
    }

    async createProduct(productData) {
        return this.request('', {
            method: 'POST',
            body: JSON.stringify({
                action: 'createProduct',
                data: productData
            })
        });
    }

    async updateProduct(id, productData) {
        return this.request('', {
            method: 'POST',
            body: JSON.stringify({
                action: 'updateProduct',
                id: id,
                data: productData
            })
        });
    }

    async deleteProduct(id) {
        return this.request('', {
            method: 'DELETE',
            body: JSON.stringify({
                action: 'deleteProduct',
                id: id
            })
        });
    }

    // Orders
    async getOrders() {
        return this.request('?action=getOrders');
    }

    async updateOrderStatus(id, status) {
        return this.request('', {
            method: 'POST',
            body: JSON.stringify({
                action: 'updateOrderStatus',
                id: id,
                status: status
            })
        });
    }

    // Dashboard
    async getDashboardStats() {
        return this.request('?action=getDashboardStats');
    }
}

export default new ApiService();
