import React, { useState } from 'react';

const CreateProductModal = ({ onClose, onAction }) => {
  const [formData, setFormData] = useState({
    nome_produto: '',
    id_categoria: '',
    preco: '',
    desconto_percentual: '0',
    e_novo: false,
    estoque: '10',
    descricao_detalhada: '',
    imagem_url: ''
  });

  const categories = [
    { id: 1, nome: 'Manga' },
    { id: 2, nome: 'Caneca' },
    { id: 3, nome: 'Acessorios' }
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aqui você integraria com a API
    console.log('Novo produto:', formData);
    onAction('product-created', formData);
    onClose();
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  return (
    <div className="modal">
      <div className="modal-header">
        <h2>Cadastrar Novo Produto</h2>
        <button className="close-btn" onClick={onClose}>×</button>
      </div>

      <form onSubmit={handleSubmit} className="modal-form">
        <div className="form-group">
          <label>Nome do Produto *</label>
          <input
            type="text"
            name="nome_produto"
            value={formData.nome_produto}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Categoria *</label>
            <select
              name="id_categoria"
              value={formData.id_categoria}
              onChange={handleChange}
              required
            >
              <option value="">Selecione...</option>
              {categories.map(cat => (
                <option key={cat.id} value={cat.id}>
                  {cat.nome}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Preço (R$) *</label>
            <input
              type="number"
              step="0.01"
              name="preco"
              value={formData.preco}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Desconto (%)</label>
            <input
              type="number"
              step="0.01"
              name="desconto_percentual"
              value={formData.desconto_percentual}
              onChange={handleChange}
            />
          </div>

          <div className="form-group">
            <label>Estoque *</label>
            <input
              type="number"
              name="estoque"
              value={formData.estoque}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        <div className="form-group">
          <label>URL da Imagem</label>
          <input
            type="url"
            name="imagem_url"
            value={formData.imagem_url}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label>Descrição Detalhada</label>
          <textarea
            name="descricao_detalhada"
            value={formData.descricao_detalhada}
            onChange={handleChange}
            rows="4"
          />
        </div>

        <div className="form-checkbox">
          <label>
            <input
              type="checkbox"
              name="e_novo"
              checked={formData.e_novo}
              onChange={handleChange}
            />
            Produto Novo
          </label>
        </div>

        <div className="modal-actions">
          <button type="button" className="btn-secondary" onClick={onClose}>
            Cancelar
          </button>
          <button type="submit" className="btn-primary">
            Cadastrar Produto
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreateProductModal;
