import React from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Typography } from '@mui/material';

const mockData = [
  { id: 1, produto: 'Notebook Pro', cliente: 'Ana Silva', valor: 'R$ 5.200,00', status: 'Entregue' },
  { id: 2, produto: 'Monitor 4K', cliente: 'Bruno Costa', valor: 'R$ 1.800,00', status: 'Pendente' },
  { id: 3, produto: 'Mouse Sem Fio', cliente: 'Carlos Melo', valor: 'R$ 120,00', status: 'Entregue' },
  { id: 4, produto: 'Webcam HD', cliente: 'Diana Lima', valor: 'R$ 350,00', status: 'Em Envio' },
];

function RecentOrdersTable() {
  return (
    <Paper sx={{ width: '100%', overflow: 'hidden' }}>
      <Typography variant="h6" component="div" sx={{ p: 2 }}>
        Últimos Pedidos
      </Typography>
      <TableContainer>
        <Table stickyHeader aria-label="tabela de pedidos recentes">
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Produto</TableCell>
              <TableCell>Cliente</TableCell>
              <TableCell align="right">Valor</TableCell>
              <TableCell>Status</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {mockData.map((row) => (
              <TableRow
                key={row.id}
                sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
              >
                <TableCell component="th" scope="row">
                  {row.id}
                </TableCell>
                <TableCell>{row.produto}</TableCell>
                <TableCell>{row.cliente}</TableCell>
                <TableCell align="right">{row.valor}</TableCell>
                <TableCell>
                  {/* Cor do status para melhor visualização */}
                  <span style={{
                    color: row.status === 'Entregue' ? 'green' : row.status === 'Pendente' ? 'red' : 'orange',
                    fontWeight: 'bold'
                  }}>
                    {row.status}
                  </span>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Paper>
  );
}

export default RecentOrdersTable;
