import React from 'react';

// Componentes Reutilizáveis (StatCard, SidebarItem - já definidos anteriormente)
const StatCard = ({ title, value, trend, isAlert, icon }) => (
  <div className="bg-slate-950 p-6 rounded-xl border border-slate-800 flex items-start justify-between">
    <div>
      <p className="text-slate-400 text-sm mb-1">{title}</p>
      <h3 className="text-2xl font-bold text-slate-100">{value}</h3>
      <p className={`text-xs mt-2 font-medium ${isAlert ? 'text-rose-400' : 'text-emerald-400'}`}>
        {trend} {isAlert ? '' : ' vs mês anterior'}
      </p>
    </div>
    <div className={`p-3 rounded-lg bg-slate-900`}>
      {icon}
    </div>
  </div>
);

// Dados Mock (Serão substituídos pela sua API PHP)
const mockOrders = [
    { id: '#0054', cliente: 'Elara V.', item: 'Cristal Quartzo Raro', status: 'Aprovado', valor: 'R$ 180,00' },
    { id: '#0053', cliente: 'Kael S.', item: 'Tinta Arcana Azul', status: 'Pendente', valor: 'R$ 45,90' },
    { id: '#0052', cliente: 'Zoe P.', item: 'Grimório Básico', status: 'Enviado', valor: 'R$ 310,00' },
];

const mockLowStock = [
    { id: 1, nome: 'Velas Negras (Avulsas)', qtd: 2 },
    { id: 2, nome: 'Essência de Sândalo', qtd: 4 },
];

const HomeOverview = ({ dataVendas, dataProdutos }) => { // Receberá dados via props ou fetch

  // Exemplo de extração de dados da API (substituir dataVendas pelo estado real)
  const receitaTotal = "R$ 15.670,00"; // Usar o total_receita do seu endpoint PHP
  const pedidosAtivos = "58"; 
  const estoqueCritico = mockLowStock.length;

  return (
    <div className="p-8">

      {/* Título e Ação Rápida */}
      <div className="flex justify-between items-end mb-8 border-b border-slate-800 pb-4">
        <div>
          <h2 className="text-3xl font-extrabold text-white">Visão Geral Arcano</h2>
          <p className="text-slate-400 mt-1">Status da Loja em Tempo Real.</p>
        </div>
        <button className="bg-violet-600 hover:bg-violet-700 text-white px-5 py-2 rounded-lg text-sm font-medium transition-colors">
          + Criar Novo Pedido
        </button>
      </div>

      {/* Seção 1: Indicadores Chave (KPIs) */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <StatCard 
          title="Receita do Mês" 
          value={receitaTotal}
          trend="+8,5%" 
          icon={<TrendingUp size={24} className="text-emerald-400" />} 
        />
        <StatCard 
          title="Pedidos Ativos" 
          value={pedidosAtivos}
          trend="Nenhum cancelamento" 
          icon={<ShoppingBag size={24} className="text-violet-400" />} 
        />
        <StatCard 
          title="Novos Clientes" 
          value="42"
          trend="+12%" 
          icon={<Users size={24} className="text-sky-400" />} 
        />
        <StatCard 
          title="Estoque Crítico" 
          value={`${estoqueCritico} Itens`}
          trend="Reposição Urgente" 
          isAlert 
          icon={<AlertCircle size={24} className="text-rose-400" />} 
        />
      </div>

      {/* Seção 2: Gráficos e Atividade Recente */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">

        {/* Coluna 1: Gráfico de Vendas (Ocupa 2 Colunas) */}
        <div className="lg:col-span-2 bg-slate-950 p-6 rounded-xl border border-slate-800 shadow-xl">
          <h3 className="font-semibold mb-6 flex items-center text-slate-200">
            <BarChart size={18} className="mr-2 text-violet-400"/>
            Tendência de Vendas (Últimos 30 Dias)
          </h3>
          {/* Espaço para o Gráfico Recharts - Será preenchido com dados do seu endpoint api_vendas.php */}
          <div className="h-64 flex items-center justify-center bg-slate-900 border border-dashed border-violet-600/30 rounded-lg">
             <p className="text-slate-500">GRÁFICO DE VENDAS RECHARTS VAI AQUI</p>
          </div>
        </div>

        {/* Coluna 2: Lista de Alertas e Pedidos (Ocupa 1 Coluna) */}
        <div className="space-y-8">

            {/* Alerta de Estoque */}
            <div className="bg-slate-950 p-6 rounded-xl border border-rose-800 shadow-xl">
                <h3 className="font-semibold mb-4 flex items-center text-rose-400">
                   <AlertCircle size={18} className="mr-2"/> 
                   ⚠️ Estoque Baixo ({estoqueCritico})
                </h3>
                <div className="space-y-3">
                    {mockLowStock.map(item => ( // USE O ESTADO REAL AQUI (dataProdutos)
                        <div key={item.id} className="flex justify-between text-sm text-slate-300">
                            <span>{item.nome}</span>
                            <span className="text-rose-400 font-bold">{item.qtd} uni.</span>
                        </div>
                    ))}
                </div>
            </div>

            {/* Pedidos Recentes */}
            <div className="bg-slate-950 p-6 rounded-xl border border-slate-800 shadow-xl">
                <h3 className="font-semibold mb-4 flex items-center text-slate-200">
                    <Clock size={18} className="mr-2 text-violet-400"/>
                    Atividade Recente
                </h3>
                <div className="space-y-4">
                    {mockOrders.map(order => ( // USE O ESTADO REAL AQUI
                        <div key={order.id} className="flex justify-between text-sm border-b border-slate-900 pb-2">
                            <div>
                                <p className="text-slate-200">{order.cliente}</p>
                                <p className="text-xs text-slate-500">{order.item}</p>
                            </div>
                            <div className="text-right">
                                <p className="font-medium text-violet-300">{order.valor}</p>
                                <span className={`text-[10px] font-bold ${order.status === 'Aprovado' ? 'text-emerald-400' : 'text-yellow-400'}`}>
                                    {order.status}
                                </span>
                            </div>
                        </div>
                    ))}
                </div>
                <button className="w-full mt-4 text-violet-500 text-sm hover:text-violet-400 transition-colors">Ver todos os pedidos</button>
            </div>
        </div>
      </div>
    </div>
  );
};

export default HomeOverview;
